import React, { useState } from 'react';
import 'ol/ol.css';
import { IMapSidebarProps } from '../types/types';
import Sidebar from './Sidebar';
import Tab from './Tab';

const MapSidebar = (props: IMapSidebarProps) => {

    const [collapsed, setCollapsed] = useState(false);
    const [selected, setSelected] = useState('home')

    function onClose() {
        setCollapsed(true);
        //@ts-ignore
        setSelected(undefined);
    }
    function onOpen(id: string) {
        setCollapsed(false)
        setSelected(id)
    };

    return (
        <>
            <Sidebar
                id='sidebar'
                collapsed={collapsed}
                selected={selected}
                onClose={onClose}
                //@ts-ignore
                doOpen={onOpen}
            >
                <Tab
                    id='home'
                    header='Home'
                    faIcon='fa fa-bars'
                >
                    <p>This is Home.</p>
                </Tab>
                <Tab
                    id='database'
                    header='Database'
                    faIcon='fa fa-database'
                >
                    <p>Data</p>
                </Tab>
                <Tab
                    id='settings'
                    header='Settings'
                    faIcon='fa fa-cogs'
                    disabled={true}
                    anchor='bottom' />
                <Tab
                    id='off'
                    header='PowerOff'
                    faIcon='fa fa-power-off'
                    anchor='bottom' />
            </Sidebar>
        </>
    )
}

export default MapSidebar;
