import React from 'react';
import { ISidebarProps } from '../types/types';

const Sidebar = (props: ISidebarProps): JSX.Element => {

    // const [map, setMap] = useState<BasemapState>(null)
    // const [activeButton] = useState(undefined)

    const {
        id,
        collapsed,
        position,
        selected,
        closeIcon,
        onClose,
        onOpen,
        children,
        anchor,
    } = props

    function renderTab(tab: any) {
        let icon;
        if (typeof (tab.props.icon) === 'string')
            icon = <i className={tab.props.icon} />;
        else if (typeof (tab.props.icon) === 'object')
            icon = tab.props.icon;
        const active = tab.props.id === selected ? ' active' : '';
        const disabled = tab.props.disabled ? ' disabled' : '';
        return (
            <li className={active + disabled} key={tab.props.id}>
                <a href={'#' + tab.props.id} role="tab" onClick={e => tab.props.disabled || onOpen(tab.props.id)}>{/*  e */}
                    {icon}
                </a>
            </li>
        );
    }

    function renderPanes(children: any) {
        return React.Children.map(children,
            p => React.cloneElement(p, {
                onClose: onClose,
                closeIcon: closeIcon,
                active: p.props.id === selected,
                position: position || 'left'
            }));
    }

    const positionStatus = ' sidebar-' + (position || 'left');
    const collapsedStatus = collapsed ? ' collapsed' : '';

    const tabs = React.Children.toArray(children).filter(c => !!c);
    const bottomtabs = tabs.filter(t => anchor === 'bottom');
    const toptabs = tabs.filter(t => anchor !== 'bottom');

    return (
        <>
            <div id={id} className={"sidebar leaflet-touch" + positionStatus + collapsedStatus}> {/* ref={el => rootElement = el} */}
                <div className="sidebar-tabs">
                    <ul role="tablist">   {/* Top-aligned */}
                        {toptabs.map(t => renderTab(t))}
                    </ul>
                    <ul role="tablist">   {/* Bottom-aligned */}
                        {bottomtabs.map(t => renderTab(t))}
                    </ul>
                </div>
                <div className="sidebar-content">
                    {renderPanes(tabs)}
                </div>
            </div>
        </>
    )
}

export default Sidebar;
