import React, { useState } from 'react';
import 'ol/ol.css';
import { IMapSidebarProps, selectableTabs } from '../types/types';
import Sidebar from './Sidebar';
import Tab from './Tab';
import ToggleButton from './ToogleButton';
import TabContent from './TabContent';
import { Feature, MapBrowserEvent } from 'ol';
import { FeatureLike } from 'ol/Feature';
import { activeToggleColor } from '../utils/MapStyles';
import Button from './Button';
import { geoserverWfsUrl, transactionParameters, wfsTransaction, geoserverHeaders, trackFeature } from '../utils/MapConfig';
import VectorLayer from 'ol/layer/Vector';
import { MapUtils } from '../utils/MapUtils';
import LineString from 'ol/geom/LineString';

const mapListeners = [];

const MapSidebar = (props: IMapSidebarProps): JSX.Element => {

    const { map } = props;

    const [collapsed, setCollapsed] = useState(true);
    const [selected, setSelected] = useState<selectableTabs>(undefined);
    const [toggleValue, setToggleValue] = useState(false);
    const [selectedFeature, setSelectedFeature] = useState(new Feature())
    const [transactionResponse, setTransactionResponse] = useState('')
    const [trackResponse, setTrackResponse] = useState('')


    function onClose() {
        setCollapsed(true);
        //@ts-ignore
        setSelected(undefined);
    }
    function onOpen(id: string) {
        setCollapsed(false)
        //@ts-ignore
        setSelected(id)
    };

    function onToggleChange() {
        setToggleValue(!toggleValue)
        selectedFeature.set('visited', !toggleValue)
    }

    const saveData = () => {
        if (selectedFeature) {
            if (selectedFeature.getId()) {
                selectedFeature.unset('selected')
                selectedFeature.getId();
                selectedFeature.setGeometryName('geom');
                selectedFeature.set('visited', toggleValue)
                let xmlString = new XMLSerializer().serializeToString(
                    wfsTransaction.writeTransaction([], [selectedFeature], [], transactionParameters)
                )
                fetch(geoserverWfsUrl, {
                    method: 'POST',
                    mode: 'no-cors',
                    headers: geoserverHeaders,
                    body: xmlString
                })
                    .then(response => setTransactionResponse(`Updated Location with id ${selectedFeature.getId()} successfully.`))
                    .then(() => {
                        //@ts-ignore
                        const layer: VectorLayer = map!.getLayers().getArray()[1]
                        layer.getSource().clear()
                        layer.getSource().refresh()
                    })
                    .catch(error => console.log(error + 'Feature could not be updated.'));
            }
            else {
                setTransactionResponse('No feature selected.')
                return;
            }
        }
        else {
            setTransactionResponse('Feature could not be updated.')
            return;
        }
    }

    const saveTrack = () => {
        let linestring = trackFeature.getGeometry() as LineString
        if (linestring) {
            trackFeature.getGeometry()
            console.log(trackFeature.getGeometryName());
            let xmlString = new XMLSerializer().serializeToString(
                wfsTransaction.writeTransaction([trackFeature], [], [], transactionParameters)
            )
            console.log(xmlString);

            fetch(geoserverWfsUrl, {
                method: 'POST',
                mode: 'no-cors',
                headers: geoserverHeaders,
                body: xmlString
            })
                .then(response => setTrackResponse(`Track inserted to database.`))
                .catch(error => console.log(error + 'Error while saving data.'));
            // }
            // else {
            //     setTrackResponse('No feature selected.')
            //     return;
            // }
        }
        else {
            setTrackResponse('No Track to save.')
            return;
        }
    }

    let prevSelected: Feature[] = []

    if (map) {
        if (mapListeners.length === 0) {
            const mapListener = map.on('click', function (e: MapBrowserEvent): void {
                map!.forEachFeatureAtPixel(e.pixel, (feature: Feature | FeatureLike): void => {
                    let castedFeature: Feature = feature as Feature;
                    prevSelected.unshift(castedFeature)
                    setSelectedFeature(prevSelected[0]);
                    prevSelected[0].set('selected', true);
                    if (prevSelected.length > 1) {
                        prevSelected[1].set('selected', false);
                    }
                    prevSelected.splice(1)
                });
            });
            mapListeners.push(mapListener);
        }
    }

    return (
        <>
            <Sidebar
                id='sidebar'
                collapsed={collapsed}
                selected={selected}
                onClose={onClose}
                //@ts-ignore
                doOpen={onOpen}
            >
                <Tab
                    id='home'
                    header='Home'
                    faIcon='fa fa-home'
                >
                    <TabContent content={
                        <>
                            <p>This is <b>CoronaTracker</b>. <br></br>
                                If you are infected with Covid19 feel free to <b>share</b> important <b>spatial information</b> with us and other citizens - <b>anonymous & voluntary</b>.
                            </p>
                            <Button 
                                buttonText='Instructions >>'
                                clickButton={() => setSelected('instructions')}
                            /> 
                        </>
                    }
                    />
                </Tab>
                <Tab
                    id='instructions'
                    header='Instructions'
                    faIcon='fa fa-info'
                />

                <Tab
                    id='locations'
                    header='Set visited locations'
                    faIcon='fa fa-mouse-pointer'
                >
                    <TabContent content={
                        <>
                            <ToggleButton
                                id='toggleVisited'
                                isOn={selectedFeature.get('visited')}
                                isOnColor={activeToggleColor}
                                handleToggle={onToggleChange}
                            />
                            {
                                JSON.stringify(selectedFeature.get('visited'))
                            }
                            <Button
                                buttonText='Update'
                                response={transactionResponse}
                                clickButton={saveData}
                            />
                        </>
                    } />
                </Tab>
                <Tab
                    id='tracking'
                    header='Tracking'
                    faIcon='fa fa-map'
                >
                    <TabContent content={
                        <>
                            <Button
                                buttonText='Start tracking'
                                clickButton={() => MapUtils.createGeolocation(map)}
                            />
                            <Button
                                buttonText='Quit tracking'
                                clickButton={saveTrack}
                                response={trackResponse}
                            />
                        </>
                    } />
                </Tab>
                <Tab
                    id='twitter'
                    header='Twitter'
                    faIcon='fa fa-twitter'
                />
                <Tab
                    id='settings'
                    header='Settings'
                    faIcon='fa fa-cogs'
                    disabled={true}
                    anchor='bottom' />
                <Tab
                    id='off'
                    header='PowerOff'
                    faIcon='fa fa-power-off'
                    anchor='bottom' />
            </Sidebar>
        </>
    )
}

export default MapSidebar;
