import React, { useState } from 'react';
import './App.scss';
import Sidebar from './components/Sidebar';
import Tab from './components/Tab';

const App: React.FC = (): JSX.Element => {

  const [collapsed, setCollapsed] = useState(false);
  const [selected, setSelected] = useState('home')

  function onClose() {
    setCollapsed(true);
    //@ts-ignore
    setSelected(undefined);
  }
  function onOpen(id: string) {
    setCollapsed(false)
    setSelected(id)
  };

  return (

    <div className='App' >
      <>
        <Sidebar
          id='sidebar'
          collapsed={collapsed}
          selected={selected}
          onClose={onClose}
          //@ts-ignore
          doOpen={onOpen}
        >
          <Tab
            id='home'
            header='Home'
            faIcon='fa fa-bars'
          >
            <p>This is Home</p>
          </Tab>
          <Tab
            id='settings'
            header='Settings'
            faIcon='fa fa-cogs'
            anchor='bottom' />
          <Tab
            id='off'
            header='PowerOff'
            faIcon='fa fa-power-off'
            anchor='bottom' />
        </Sidebar>

      </>
    </div >

  );
}


export default App;
