import React, { useState } from 'react';
import './App.css';
import Sidebar from './components/Sidebar';
import Tab from './components/Tab';

const App: React.FC = (): JSX.Element => {

  const [collapsed, setCollapsed] = useState(false);
  const [selected, setSelected] = useState('home')

  function onClose() {
    setCollapsed(true);
  }
  function onOpen(id: string) {
    setCollapsed(false)
    setSelected(id)
  };

  return (

    <div className='App'>
      <>
        <Sidebar
          id='sidebar'
          collapsed={true}
          onClose={onClose}
          onOpen={onOpen}
        >
          <Tab id='home' header='Home' faIcon='fa fa-bars' />
          <Tab id='settings' header='Settings' faIcon='fa fa-cogs' />
          <Tab id='off' header='PowerOff' faIcon='fa fa-power-off' />
        </Sidebar>

      </>
    </div >

  );
}


export default App;
