import React, { useState } from 'react';
import { IInstructionProps } from '../types/types';
import Button from './Button';
import { usedIcons } from '../utils/MapConfig';

const Instructions = (props: IInstructionProps) => {

    const [open, setOpen] = useState([false, false]);

    function showInstructions(e: any) {
        const activeState = [false, false]
        activeState[e.currentTarget.index] = false ? true : false;
        setOpen([true, false])
    }

    return (
        <>
            <Button
                buttonText={`Set visited location `}
                faIcon={open[0] ? usedIcons[8] : usedIcons[7]}
                clickButton={showInstructions}
            />
            <div className={open[0] ? 'notCollapsedInst' : 'collapsedInst'}>
                <li >Tab/Click a building you have visited within two weeks before your diagnosis. A selected building gets a thick orange border.</li>
                <li>Select <i className={usedIcons[2]}></i> from the menu. Set a visited builiding to true with the ToggleButton. </li>
                <li>Click Update. The map view will refresh and the selected building will change its color.</li>
            </div>
            <Button
                buttonText={`Track position... `}
                faIcon={open[1] ? usedIcons[8] : usedIcons[7]}
                clickButton={showInstructions}
            />
            <div className={open[1] ? 'notCollapsedInst' : 'collapsedInst'}>
                <li>Select <i className={usedIcons[3]}></i> for tracking. You have to enable Geolocation to use this Feature.</li>
                <li>Everytime your position changes, your track will be updated on the map.</li>
                <li>Click 'Quit Tracking' and your data will be send to the database.</li>
            </div>
        </>
    )
}

export default Instructions;
