import { Map, View } from 'ol';
import TileLayer from 'ol/layer/Tile';
import XYZSource from 'ol/source/XYZ';
import { baseLayerUrl, mapCenter, mapZoom, rotationStatus } from './MapConfig';
import VectorSource from 'ol/source/Vector';
import GeoJSON from 'ol/format/GeoJSON';
import { Projections } from '../types/types';

export class MapUtils {
    static createBaseLayer() {
        const xyzURL: string = baseLayerUrl;
        const baseSource: XYZSource = new XYZSource({
            url: xyzURL
        });
        const baseLayer: TileLayer = new TileLayer({
            source: baseSource
        });

        return baseLayer;
    }

    static createMap() {
        const baseLayer: TileLayer = this.createBaseLayer();
        const map: Map = new Map({
            target: 'map',
            layers: [baseLayer],
            view: new View({
                center: mapCenter,
                zoom: mapZoom,
                enableRotation: rotationStatus,
            })
        });
        return map;
    }
}
