import React, { useState } from 'react';
import 'ol/ol.css';
import { IMapSidebarProps } from '../types/types';
import Sidebar from './Sidebar';
import Tab from './Tab';
import ToggleButton from './ToogleButton';

const MapSidebar = (props: IMapSidebarProps): JSX.Element => {

    const [collapsed, setCollapsed] = useState(false);
    const [selected, setSelected] = useState('home');
    const [toggleValue, setToggleValue] = useState(false);

    function onClose() {
        setCollapsed(true);
        //@ts-ignore
        setSelected(undefined);
    }
    function onOpen(id: string) {
        setCollapsed(false)
        setSelected(id)
    };

    function onToggleChange() {
        setToggleValue(!toggleValue)
    }

    return (
        <>
            <Sidebar
                id='sidebar'
                collapsed={collapsed}
                selected={selected}
                onClose={onClose}
                //@ts-ignore
                doOpen={onOpen}
            >
                <Tab
                    id='home'
                    header='Home'
                    faIcon='fa fa-bars'
                >
                    <p>This is Home.</p>
                </Tab>
                <Tab
                    id='database'
                    header='Database'
                    faIcon='fa fa-database'
                >
                    <ToggleButton
                    id='toggleLayer1'
                    isOn={toggleValue}
                    isOnColor='rgba(55,156,214,1)'
                    handleToggle={onToggleChange}
                    layerName='FlÃ¼sse'
                    />
                    <ToggleButton
                    id='toggleLayer2'
                    isOn={toggleValue}
                    isOnColor='rgba(55,156,214,1)'
                    handleToggle={onToggleChange}
                    />
                    <ToggleButton
                    id='toggleLayer3'
                    isOn={toggleValue}
                    isOnColor='rgba(55,156,214,1)'
                    handleToggle={onToggleChange}
                    />
                </Tab>
                <Tab
                    id='settings'
                    header='Settings'
                    faIcon='fa fa-cogs'
                    disabled={true}
                    anchor='bottom' />
                <Tab
                    id='off'
                    header='PowerOff'
                    faIcon='fa fa-power-off'
                    anchor='bottom' />
            </Sidebar>
        </>
    )
}

export default MapSidebar;
